inline int hash(int num)
{
    int dig[10],i=9,j,sum,anti;
    if(num==0)return -1;
    while(num) dig[i]=num%10,num/=10,i--;
    sum=(9-dig[9])*fac_n[8];
    for(i=1;i9)return 0;
        t0=9-num%10+1-3;
        t1=num/e10[t0];
        t2=t1%1000;
